// Find all `tCountryInfo` elements
def countries = xml.Body.FullCountryInfoAllCountriesResponse.FullCountryInfoAllCountriesResult.tCountryInfo

// Iterate over each country and verify required properties
countries.each { country ->
    // Assert that each required property exists and is not empty
    assert country.sISOCode.text() : "sISOCode is missing or empty for a country"
    assert country.sName.text() : "sName is missing or empty for ${country.sISOCode.text()}"
    assert country.sCapitalCity.text() : "sCapitalCity is missing or empty for ${country.sISOCode.text()}"
    assert country.sPhoneCode.text() : "sPhoneCode is missing or empty for ${country.sISOCode.text()}"
    assert country.sContinentCode.text() : "sContinentCode is missing or empty for ${country.sISOCode.text()}"
    assert country.sCountryFlag.text() : "sCountryFlag is missing or empty for ${country.sISOCode.text()}"

   // Assert if sCurrencyISOCode exists or not
if (country.sCurrencyISOCode) {
    // Assert that the element is present
    assert country.sCurrencyISOCode : "sCurrencyISOCode element is missing for country ${country.sISOCode.text()}"

    // Optionally check if it contains text and handle accordingly
//    if (country.sCurrencyISOCode.text()) {
//        log.info "Country ${country.sISOCode.text()} has currency ISO code ${country.sCurrencyISOCode.text()}."
//    } else {
//        log.info "Country ${country.sISOCode.text()} has a currency code element, but it is empty."
//    }
} else {
    // Fail the assertion if the element itself is missing
    assert false : "sCurrencyISOCode element is missing for country ${country.sISOCode.text()}"
}

    // Assert that Languages exist and have entries if present
    def languages = country.Languages.tLanguage
    if (languages.size() > 0) {
        languages.each { language ->
            assert language.sISOCode.text() : "Language sISOCode is missing for ${country.sISOCode.text()}"
            assert language.sName.text() : "Language sName is missing for ${country.sISOCode.text()}"
        }
//        log.info "Country ${country.sISOCode.text()} has ${languages.size()} language(s) with valid properties."
    } else {
//        log.info "Country ${country.sISOCode.text()} has no languages listed."
    }
}

