def response = messageExchange.responseContent
def xml = new XmlSlurper().parseText(response)

// Assertion on the main structure
assert xml.Body.ListOfCountryNamesGroupedByContinentResponse.ListOfCountryNamesGroupedByContinentResult.tCountryCodeAndNameGroupedByContinent.size() > 0 : "No continents found."

// Iterating over each continent and validating its structure
xml.Body.ListOfCountryNamesGroupedByContinentResponse.ListOfCountryNamesGroupedByContinentResult.tCountryCodeAndNameGroupedByContinent.each { continent ->

    // Assert each continent has a code and name
    assert continent.Continent.sCode.text() : "Continent code is missing."
    assert continent.Continent.sName.text() : "Continent name is missing for continent with ISO code ${continent.Continent.sCode.text()}"

    // Assert each continent has at least one country
    assert continent.CountryCodeAndNames.tCountryCodeAndName.size() > 0 : "No countries found for continent ${continent.Continent.sName.text()}."

    // Iterating over each country within the continent
    continent.CountryCodeAndNames.tCountryCodeAndName.each { country ->

        // Assert each country has an ISO code and a name
        assert country.sISOCode.text() : "Country ISO code is missing for a country in continent ${continent.Continent.sName.text()}."
        assert country.sName.text() : "Country name is missing for ISO code ${country.sISOCode.text()}."
    }
}

def response = messageExchange.responseContent
def xml = new XmlSlurper().parseText(response)

assert xml.Body.ListOfCurrenciesByCodeResponse.ListOfCurrenciesByCodeResult.tCurrency.size() > 0 : "No currencies found."

xml.Body.ListOfCurrenciesByCodeResponse.ListOfCurrenciesByCodeResult.tCurrency.each { currency ->

    assert currency.sISOCode : "ISO code element is missing in a currency entry."

    if (!currency.sISOCode.text()) {
        println "Warning: ISO code is empty in a currency entry. Skipping entry."
        return
    }

    assert currency.sName.text() : "Currency name is missing for ISO code ${currency.sISOCode.text()}."
}

